INSERT INTO `news_pool` VALUES (000000000001,000000016208,'NWS_27042020_FTP_CBE_2_SARAYA_URAL_KOVAI_NORTH','கொரோனா வைரஸ் பரவலைத் தடுக்க ஊரடங்கு அமல்படுத்தப்பட்டு உள்ளது இதனால்\nடாஸ்மாக் கடைகள் மூடப்பட்டு உள்ளதால் சாராய காய்ச்சல்கள் அதிக அளவில் அரங்கேறி\nவருகின்றன., \n\nசாராய காய்ச்சல்கள் அதிகரித்தல் காரணமாக\nபோலீசார் அதிரடி சோதனையை மேற்கொண்டு வருகின்றனர்., \n\nஅதனை போலீசார்\nகைப்பற்றி அழித்தனர்., \n\n',176,'EN',28,'',''),(000000000002,000000016208,'NWS_27042020_FTP_CBE_2_SARAYA_URAL_KOVAI_NORTH','கொரோனா வைரஸ் பரவலைத் தடுக்க ஊரடங்கு அமல்படுத்தப்பட்டு உள்ளது இதனால்\nடாஸ்மாக் கடைகள் மூடப்பட்டு உள்ளதால் சாராய காய்ச்சல்கள் அதிக அளவில் அரங்கேறி\nவருகின்றன., \n\nசாராய காய்ச்சல்கள் அதிகரித்தல் காரணமாக\nபோலீசார் அதிரடி சோதனையை மேற்கொண்டு வருகின்றனர்., \n\nஅவரிடமிருந்து பறிமுதல் செய்யப்பட்டு சாராய ஊறல்\nஅழிக்கப்பட்டது., \n\nஇதனை தடுக்க மதுவிலக்கு போலீசார் தீவிர கண்காணிப்பில் ஈடுபட்டு\nவருகின்றனர்., \n\nஅப்போது சாராய ஊறல் பேரலுடன் இருந்த மருதாசலம் என்பவரை\nபோலீசார் கைது செய்தனர்., \n\nஅப்போது அங்கு உள்ள பழைய கட்டிடத்தில் ஒரு பேரலில்\n150 லிட்டர் சாராய ஊறல் வைக்கப்பட்டிருந்தது தெரியவந்தது., \n\nகடந்த 5 நாட்களில் மட்டும் சுமார் 600 லிட்டர் சாராய ஊறல்\nஅழிக்கப்பட்டது குறிப்பிடத்தக்கது., \n\nகோவை தடாகத்தில் சாராய காய்ச்ச வைக்கப்பட்டு இருந்த 600 லிட்டர் ஊறல் அழிப்பு\n ஒருவர் கைது., \n\n',176,'VO',80,'',''),(000000000003,000000016208,'NWS_27042020_FTP_CBE_2_SARAYA_URAL_KOVAI_NORTH','கொரோனா வைரஸ் பரவலைத் தடுக்க ஊரடங்கு அமல்படுத்தப்பட்டு உள்ளது இதனால்\nடாஸ்மாக் கடைகள் மூடப்பட்டு உள்ளதால் சாராய காய்ச்சல்கள் அதிக அளவில் அரங்கேறி\nவருகின்றன., \n\nசாராய காய்ச்சல்கள் அதிகரித்தல் காரணமாக\nபோலீசார் அதிரடி சோதனையை மேற்கொண்டு வருகின்றனர்., \n\n',176,'HL',25,'',''),(000000000004,000000016209,'NWS_270420_FTP_TVM_04_CORONA_AFFECT_MASS_CLEANING_SCRIPT','வந்தவாசி அடுத்த சத்தியவாடி கிராமத்தில் கடந்த 25ஆம் தேதி அன்று ஒருவருக்கு கொரோனா\nதொற்று உறுதியானது இதையடுத்து அவர் விழுப்புரத்தில் உள்ள முண்டியம்பாக்கம்\nஅரசு மருத்துவமனையில் அனுமதிக்கப்பட்டார்., \n\nபேட்டி பெயர் குமார் சத்தியவாடி கிராம பொதுமக்கள்\n ., \n\n',94,'EN',26,'',''),(000000000005,000000016209,'NWS_270420_FTP_TVM_04_CORONA_AFFECT_MASS_CLEANING_SCRIPT','வந்தவாசி அடுத்த சத்தியவாடி கிராமத்தில் கடந்த 25ஆம் தேதி அன்று ஒருவருக்கு கொரோனா\nதொற்று உறுதியானது இதையடுத்து அவர் விழுப்புரத்தில் உள்ள முண்டியம்பாக்கம்\nஅரசு மருத்துவமனையில் அனுமதிக்கப்பட்டார்., \n\nதிருவண்ணாமலை மாவட்டம் வந்தவாசி அடுத்த சத்தியவாடி கிராமத்தில் ஒருவருக்கு\nகொரோனா\nதொற்று உறுதியான நிலையில் கிராமம் முழுவதும் கிருமி நாசினி தெளிக்கப்பட்டு\nமருத்துவ பரிசோதனை செய்யப்பட்டு வருகிறது., \n\nஇந்த நிலையில் சத்தியவாடி\nகிராமத்தில் மருத்துவ குழுவினர் கொரோனா தொற்று பாதிக்கப்பட்டவரின் தொடர்பில்\nஉள்ள 52 பேருக்கு\nகொரோனா பரிசோதனை செய்யப்பட்டது., \n\nவந்தவாசி அருகே ஒருவருக்கு கொரோனா உறுதியான நிலையில் கிருமி நாசினி\nதெளிக்கப்பட்டு மருத்துவ பரிசோதனை செய்யப்பட்டு வருகிறது., \n\nபேட்டி பெயர் குமார் சத்தியவாடி கிராம பொதுமக்கள்\n ., \n\nமேலும் சத்தியவாடி கிராமத்தை\nசுற்றியுள்ள பல்வேறு கிராமங்களில் பொதுமக்கள் தானாகவே முன்வந்து தடுப்பு வேலி\nஅமைத்துக் கொண்டனர்., \n\n',94,'VO',79,'',''),(000000000006,000000016209,'NWS_270420_FTP_TVM_04_CORONA_AFFECT_MASS_CLEANING_SCRIPT','வந்தவாசி அடுத்த சத்தியவாடி கிராமத்தில் கடந்த 25ஆம் தேதி அன்று ஒருவருக்கு கொரோனா\nதொற்று உறுதியானது இதையடுத்து அவர் விழுப்புரத்தில் உள்ள முண்டியம்பாக்கம்\nஅரசு மருத்துவமனையில் அனுமதிக்கப்பட்டார்., \n\nபேட்டி பெயர் குமார் சத்தியவாடி கிராம பொதுமக்கள்\n ., \n\n',94,'HL',26,'',''),(000000000007,000000016210,'NWS_270420_FTP_PDK_5_LATTU_POLICE','லட்டுவேப்பிலை  கொடுத்து இரு சக்கர வாகனத்தில் வருபவர்களுக்கு நாகுடி\nபோலீசார் நூதன முறையில் விழிப்புணர்வு ஏற்படுத்தினர்., \n\nஅறந்தாங்கி 27.04.2020\nதர்மராஜா., \n\nகொரோனா வைரஸ் தாக்கத்தினால்  நாடு முழுவதும் 144 தடை உத்தரவு\nபிறப்பிக்கப்பட்டுள்ளது., \n\n',97,'EN',24,'',''),(000000000008,000000016210,'NWS_270420_FTP_PDK_5_LATTU_POLICE','முககவசம் தலைகவசம் அணிந்து\nவரும் வாகன ஒட்டுநர்களுக்கு லட்டும், முககவசம் அணியாமல் வருபவர்களுக்கு\nவேப்பிலை கொடுத்தும் நூதன முறையில்  விழிப்புணர்வு ஏற்படுத்தினர்.மேலும்\nதுணியை கட்டி வருபவர்களுக்கு முககவசம் வழங்கியும் வீட்டிற்கு சென்